mapper.insert:


	select seq_board.nextval 
	from dual

insert into board(
	bo_no
	, bo_title
	, bo_content
	, bo_writer
	, bo_date
) values (
	#{boNo}
	,#{boTitle}
	,#{boContent}
	,#{boWriter}
	, sysdate
)

mapper.select:
select b.* from ( select a.* , row_number() over(order by a.bo_no desc) rnum from( select bo_no , bo_title , bo_content , bo_writer , bo_date , bo_hit from board where 1=1
order by bo_no desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.update:

		update board
		set
             bo_title = #{boTitle}
             , bo_content = #{boContent}
             , bo_date = sysdate
        where bo_no = #{boNo}
