mapper.insert:


	select seq_free.nextval 
	from dual

insert into free(
	free_no
	, free_title
	, free_content
	, free_writer
	, free_date 
) values (
	#{freeNo}
	, #{freeTitle}
	, #{freeContent}
	, #{freeWriter}
	, sysdate
)

mapper.select:
select b.* from ( select a.* , row_number() over(order by a.free_no desc) rnum from( select free_no , free_title , free_content , free_writer , free_date , free_hit from free where 1=1
order by free_no desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.update:

		update free
		set
             free_title = #{freeTitle}
             , free_content = #{freeContent}
             , free_date = sysdate
        where free_no = #{freeNo}
