mapper.insert:


	select seq_notice.nextval 
	from dual

insert into notice(
	no_no
	, no_title
	, no_content
	, no_writer
	, no_date
) values (
	#{noNo}
	,#{noTitle}
	,#{noContent}
	,#{noWriter}
	, sysdate
)

mapper.select:
select b.* from ( select a.* , row_number() over(order by a.no_no desc) rnum from( select no_no , no_title , no_content , no_writer , no_date , no_hit from notice where 1=1
order by no_no desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.update:

		update notice
		set
             no_title = #{noTitle}
             , no_content = #{noContent}
             , no_date = sysdate
        where no_no = #{noNo}
