mapper.insert:


	select seq_notice.nextval 
	from dual

insert into notice(
	notice_no
	, notice_title
	, notice_content
	, notice_writer
	, notice_date
) values (
	#{noticeNo}
	,#{noticeTitle}
	,#{noticeContent}
	,#{noticeWriter}
	, sysdate
)

mapper.select:
select b.* from ( select a.* , row_number() over(order by a.notice_no desc) rnum from( select notice_no , notice_title , notice_content , notice_writer , notice_date , notice_hit from notice where 1=1
order by notice_no desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.update:

		update notice
		set
             notice_title = #{noticeTitle}
             , notice_content = #{noticeContent}
             , notice_date = sysdate
        where notice_no = #{noticeNo}
